INSERT INTO `rec_modo_preparo` VALUES (1,1,'Em uma batedeira, bata ligeiramente a manteiga e, mantendo-a ligada, acrescentando as gemas dos ovos e o Leite MOÇA em fio, até obter um creme bem leve e esbranquiçado (cerca de 20 minutos batendo em velocidade média)');
INSERT INTO `rec_modo_preparo` VALUES (2,1,'Desligue a batedeira e junte ao creme as nozes, o Chocolate ALPINO e a essência de baunilha, misturando delicadamente com um batedor de arame ou colher de cabo longo');
INSERT INTO `rec_modo_preparo` VALUES (3,1,'Peneire aos poucos sobre o creme, a farinha de trigo, o bicarbonato e o sal e vá misturando com o batedor de arame, incorporando bem os ingredientes secos ao creme');
INSERT INTO `rec_modo_preparo` VALUES (4,1,'Em 2 assadeiras grandes, untadas com manteiga e polvilhadas com farinha de trigo, coloque pequenas porções de massa (use como medida uma colher de chá bem cheia de massa), mantendo uma distância aproximada de 10cm entre elas');
